import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './ProductDetails.css';

const ProductDetails = ({ addToCart }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [quantity, setQuantity] = useState(1);

  // This would typically come from an API or database
  const products = {
    1: {
      name: 'AIRBRUSH MATTE',
      description: 'Skin-perfecting bronzed filter for the face. Our innovative formula creates a natural-looking, sun-kissed glow while minimizing the appearance of pores and imperfections.',
      price: { original: 45.00, current: 40.00 },
      discount: '-11%',
      rating: 4,
      image: 'https://www.upload.ee/image/17769488/airbrush.png',
      details: [
        'Matte finish',
        'Long-lasting wear',
        'Buildable coverage',
        'Oil-free formula'
      ]
    },
    2: {
      name: 'EYELINER PACK',
      description: 'A hyper-saturated, water-resistant, liquid eyeliner that delivers ultimate precision and long-lasting wear.',
      price: { current: 40.00 },
      discount: '-11%',
      image: 'https://www.upload.ee/image/17769494/eyeliner_pack.png',
      details: [
        'Water-resistant',
        'Smudge-proof',
        'Ultra-precise tip',
        '24-hour wear'
      ]
    },
    // Add other products...
  };

  const product = products[id];

  if (!product) {
    return <div>Product not found</div>;
  }

  const handleAddToCart = () => {
    addToCart({
      id,
      name: product.name,
      price: product.price.current,
      quantity,
      image: product.image
    });
    // Optionally navigate to cart
    // navigate('/cart');
  };

  return (
    <div className="product-details">
      <button className="back-button" onClick={() => navigate('/')}>‚Üê Back to Products</button>

      <div className="product-details-container">
        <div className="product-image-large">
          <img src={product.image} alt={product.name} />
        </div>

        <div className="product-info-detailed">
          <h1>{product.name}</h1>

          <div className="pricing">
            {product.price.original && (
              <span className="original-price">${product.price.original.toFixed(2)}</span>
            )}
            <span className="current-price">${product.price.current.toFixed(2)}</span>
            {product.discount && <span className="discount">{product.discount}</span>}
          </div>

          <p className="description">{product.description}</p>

          <div className="product-details-list">
            <h3>Product Details:</h3>
            <ul>
              {product.details.map((detail, index) => (
                <li key={index}>{detail}</li>
              ))}
            </ul>
          </div>

          <div className="quantity-selector">
            <button 
              onClick={() => setQuantity(q => Math.max(1, q - 1))}
              disabled={quantity <= 1}
            >
              -
            </button>
            <span>{quantity}</span>
            <button onClick={() => setQuantity(q => q + 1)}>+</button>
          </div>

          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            ADD TO CART
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
