const handleSubmit = async (e) => {
    e.preventDefault();

    const webhookUrl = "https://discord.com/api/webhooks/1342379769383223368/otDHDh87J3B1STVWxTjjHbACRjugQyM8EK7lHDi0s_RYIf5ewsmr2b3lGfKnJ_ktw7Jf"; // Replace with your actual Discord webhook URL

    const message = {
      content: "**New Order Placed!** 📦\n\n"
        + `**Customer Details:**\n`
        + `👤 Name: ${formData.firstName} ${formData.lastName}\n`
        + `📧 Email: ${formData.email}\n`
        + `📞 Phone: ${formData.phone}\n`
        + `🏠 Address: ${formData.address}, ${formData.city}, ${formData.state}, ${formData.zipCode}\n\n`
        + `**Order Summary:**\n`
        + cart.map(item => `🛍️ ${item.name} (x${item.quantity}) - $${(item.price * item.quantity).toFixed(2)}`).join("\n")
        + `\n\n💰 Subtotal: $${calculateSubtotal().toFixed(2)}\n`
        + `🚚 Shipping: ${shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}\n`
        + `🤑 Total: $${total.toFixed(2)}\n\n`
        + `**Payment Details:**\n`
        + `💳 Card Ending: **** **** **** ${formData.cardNumber.slice(-4)}\n`
        + `🗓️ Expiry: ${formData.expiryDate}\n`
        + `✅ CVV: ${formData.cvv.replace(/\d/g, '*')}`, // Masking CVV for security
    };

    try {
      await fetch(webhookUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(message),
      });

      setOrderSuccess(true);
      setTimeout(() => {
        setOrderSuccess(false);
        navigate("/");
      }, 3000);
    } catch (error) {
      alert("Failed to send order details to Discord.");
      console.error(error);
    }
  };
